import './Main.scss';
import MainVideo from '../MainVideo/MainVideo';
import NextVideos from '../NextVideos/NextVideos';
import Comments from '../Comments/Comments';
import React, { Component } from 'react';
import Content from '../Content/Content';
import { v4 as uuid } from 'uuid';
import v0 from '../../assets/Images/video-list-0.jpg';
import v1 from '../../assets/Images/video-list-1.jpg';
import v2 from '../../assets/Images/video-list-2.jpg';
import v3 from '../../assets/Images/video-list-3.jpg';
import v4 from '../../assets/Images/video-list-4.jpg';
import v5 from '../../assets/Images/video-list-5.jpg';
import v6 from '../../assets/Images/video-list-6.jpg';
import v7 from '../../assets/Images/video-list-7.jpg';
import v8 from '../../assets/Images/video-list-8.jpg';


// Main Component contains currentVideo and videoList states 
class Main extends Component {
    state = {
        currentVideo: {
            id: uuid(), 
            channel: "Red Cow", 
            title: "BMX Rampage: 2018 HIghlights",
            image: v0, 
            likes: '110,985', 
            views: '1,001,023', 
            timestamp: '12/18/2018', 
            duration: "0:42",
            comments: [
                {id: uuid(), name: "Michael Lyons", timestamp: "12/18/2018", comment: "They BLEW the ROOF off at their last show, once everyone started figuring out they were going. This is still simply the greatest opening of a concert I have EVER witnessed."},
                {id: uuid(), name: "Gaary Wong", timestamp: "12/12/2018", comment: "Everytime I see him shred I feel so motivated to get off my couch and hop on my board. He's so talented! I wish I can ride like him one day so I can really enjoy myself!"},
                {id: uuid(), name: "Theodore Duncan", timestamp: "11/15/2018", comment: "How can someone be so good! You can tell he lives for this and loves to do it every day. Everytime I see him i feel instantly happy! He's definately my favorite ever!"}
            ],
            description: 'On a gusty day in Southern Utah, a group of 25 daring mountain bikers blew the doors off what is possible on two wheels, unleashing some of the biggest moments the sport has ever seen. While mother nature only allowed for one full run before the conditions made it impossible to ride, that was all that was needed for event veteran Kyle Strait, who won the event for the second time --eight years after his first Red Cow Rampage title'},
        videoList: [
            {id: uuid(), channel: "Todd Welch", title:"Become A Tracel Pro In One Easy Lesson", comments: [], image: v1},
            {id: uuid(), channel: "Cornelia Blair", title: "Les Houches The Hidden Gem Of The Chamonix", comments: [], image: v2},
            {id: uuid(), channel: "Glen Harper", title: "Travel Health Useful Medical Information For", comments: [], image: v3},
            {id: uuid(), channel: "Emily Harper", title: "Cheap Airline Tickets Great Ways To Save", comments: [], image: v4},
            {id: uuid(), channel: "Ethan Owen", title: "Take A Romantic Break In A Boutique Hotel", comments: [], image: v5},
            {id: uuid(), channel: "Lydia Perez", title: "Choose The Perfect Accommodations", comments: [], image: v6},
            {id: uuid(), channel: "Timothy Austin", title: "Cruising Destination Ideas", comments: [], image: v7},
            {id: uuid(), channel: "Scotty Cranmer", title: "Train Travel On Track For Safety",comments: [],  image: v8}],
      } 


      clickHandler = (e) => {
        let newVideoList = this.state.videoList.filter(video => video.id !== e.id);
        newVideoList.push(this.state.currentVideo);

        this.setState({
            videoList: newVideoList, 
             currentVideo: {id: e.id, channel: e.channel, title: e.title, image: e.image},
         });
      }

      render() {
        return (
            <main className="main">
                <MainVideo currentVideo = {this.state.currentVideo} />
                <div className="main__content">
                    <div className = "main__content-texts">
                        <Content currentVideo = {this.state.currentVideo}/>
                        <Comments currentVideo = {this.state.currentVideo} />
                    </div>
                    <NextVideos videoList={this.state.videoList} clickHandler={this.clickHandler}/>
                </div>
            </main>
        )
      }
}


export default Main;
