import React from 'react'
import play from '../../assets/Icons/SVG/play.svg';
import pause from '../../assets/Icons/SVG/pause.svg';

function toggleButton(bool) {
    if (bool == false) {
        return <img alt="play button" className ="video__play-btn"src={pause}></img>
    }
    return <img alt="play button" className ="video__play-btn"src={play}></img>
}

export default function VideoButton(props) {

    return (
        <div>
            <button onClick={props.addTimerEvent}className ="video__play" id="play">
                {toggleButton(props.playToggle)}
            </button>

        </div>
    )
}
