import './Main.scss';
import MainVideo from '../MainVideo/MainVideo';
import React, { Component } from 'react';
import v0 from '../../assets/Images/video-list-0.jpg';
import v1 from '../../assets/Images/video-list-1.jpg';
import v2 from '../../assets/Images/video-list-2.jpg';
import v3 from '../../assets/Images/video-list-3.jpg';
import v4 from '../../assets/Images/video-list-4.jpg';
import v5 from '../../assets/Images/video-list-5.jpg';
import v6 from '../../assets/Images/video-list-6.jpg';
import v7 from '../../assets/Images/video-list-7.jpg';
import v8 from '../../assets/Images/video-list-8.jpg';

let title = 'BMX Rampage: 2018 Highlights';
let publisher = 'By Red Cow';
let datePublished = '12/18/2018';
let views = '1,001,023';
let likes = '110,985';
// let article = 'On a gusty day in Southern Utah, a group of 25 daring mountain bikers blew the doors off what is possible on two wheels, unleashing some of the biggest moments the sport has ever seen. While mother nature only allowed for one full run before the conditions made it impossible to ride, that was all that was needed for event veteran Kyle Strait, who won the event for the second time --eight years after his first Red Cow Rampage title'

class TitleBar extends Component {
      render() {
          return (
              <div>
                  <h1 className="title">{title}</h1>
                  <div className="title__stat-bar">
                      <h2 className="title__publisher">{publisher}</h2>
                      <p>{datePublished}</p>
                      <p>{views}</p>
                      <p>{likes}</p>
                  </div>
              </div>
          )
      }
}

class Main extends Component {
    state = {
        currentVideo: v0,
        videoList: [v0, v1, v2, v3, v4, v5, v6, v7, v8],
      }
      render() {
        return (
            <main className="main">
                <MainVideo currentVideo = {this.state.currentVideo} />
                {/* <TitleBar /> */}
            </main>
        )

      }

}

export default Main;
