import React, {Component} from 'react'
import Main from '../../components/Main/Main';
import './HomePage.scss';
import axios from 'axios'
import {BrowserRouter, Route, Switch} from 'react-router-dom'



let firstVideoId = '1af0jruup5gu';

let apiKey = '?api_key=c4f92712-c6b3-47a0-abb5-1c487b16a781';
let url ='https://project-2-api.herokuapp.com/videos/';



class HomePage extends Component {
    state = {
        currentVideo: {comments: [{comment: ""}]},
        videoList: []
      } 

      componentDidMount() {
        axios.get(url + apiKey)
        .then(res => {
            this.setState({
                videoList: res.data,
            })
        })

        axios.get(url + firstVideoId + apiKey)
        .then(res=> {
            this.setState({
                currentVideo: res.data
            })
        })
    }

    // clickHandler() {
    //     // console.log(typeof this.props.match.params.videoId)
    //     if (typeof this.props.match.params.videoId !== "undefined") {
    //         let item = this.state.videoList.find((video)=> video.id === this.props.match.params.videoId)
    //         console.log(item.id)
    //         if (item.id !== this.state.currentVideo.id) {
    //                   axios.get(url + item.id + apiKey)
    //                   .then(res=> {
    //                       this.setState({
    //                           currentVideo: res.data,
    //                       })
    //                   })
    //               }
    //     }
    // }

    componentDidUpdate() {

        let item = this.state.videoList.find((video)=> video.id === this.props.match.params.videoId)
            // console.log(item.id)

            if (item.id && item.id !== this.state.currentVideo.id) {

                      axios.get(url + item.id + apiKey)
                      .then(res=> {
                          this.setState({
                              currentVideo: res.data,
                          })
                      })
                  }
        }


    render() {
        // console.log(this.props.match.params)
        return (
            <>
                <Main currentVideo={this.state.currentVideo} videoList={this.state.videoList} clickHandler={this.clickHandler}/>
                <Route path={`${this.props.match.url}/videoId`} component={(routerProps)=><Main {...routerProps} currentVideo={this.state.currentVideo} videoList={this.state.videoList}/>}/>
            </>
        )
    }
}


export default HomePage
